void matrix_mul_const(unsigned int N, signed int *C, signed short *A, signed short val)
{
    unsigned int i, j;
    for (i = 0; i < N; i++)
    {
        for (j = 0; j < N; j++)
        {
            C[i * N + j] = (signed int)A[i * N + j] * (signed int)val;
        }
    }
}
