// CHECK-LABEL: @setcs_asm(
// CHECK-NEXT:  entry:
// CHECK-NEXT:    tail call void asm sideeffect "set $$cs = $0", "r,~{$cs}"(i64 [[L:%.*]]) #[[ATTR3:[0-9]+]], !srcloc [[META2:![0-9]+]]
// CHECK-NEXT:    [[ADD:%.*]] = fadd float [[A:%.*]], [[B:%.*]]
// CHECK-NEXT:    ret float [[ADD]]
//
float setcs_asm(long l, float a, float b) {
  __asm__ volatile("set $cs = %0"
                   :
                   : "r"(l)
                   : "$cs");
  return a + b;
}

